trigger_input = $node['Trigger'].json if isinstance($node['Trigger'].json, dict) else {}
trigger_product_type_id = trigger_input.get('product_type_id')
trigger_domain = trigger_input.get('domain')

try:
    trigger_product_type_id = int(trigger_product_type_id) if trigger_product_type_id is not None else None
except Exception:
    trigger_product_type_id = None

products = ($node['Get PT Targets/Products'].json.get('results') or [])
scans_resp = $json if isinstance($json, dict) else {}
active = 0
for s in scans_resp.get('scans', []) or []:
    status = (s.get('current_session') or {}).get('status', '')
    if status in ['processing', 'scheduled', 'queued', 'starting']:
        active += 1

capacity = max(0, 5 - active)

eligible = []
for p in products:
    if not isinstance(p, dict):
        continue

    product_type_id = p.get('prod_type')
    try:
        product_type_id = int(product_type_id)
    except Exception:
        continue

    if trigger_product_type_id is not None and product_type_id != trigger_product_type_id:
        continue

    tags = p.get('tags') or []
    if not isinstance(tags, list):
        tags = [str(tags)]

    if 'targets:ready' in tags and 'acunetix:active' not in tags:
        eligible.append({
            'product_id': p.get('id'),
            'product_name': p.get('name'),
            'product_type_id': product_type_id,
            'domain': trigger_domain,
        })

selected = eligible[:capacity]
return [{
  'json': {
    'active_scans': active,
    'scan_limit': 5,
    'available_slots': capacity,
    'dispatch_count': len(selected),
    'dispatch_items': selected
  }
}]

out = []
for row in $json.get('dispatch_items', []):
    if not isinstance(row, dict):
        continue
    out.append({
        'json': {
            'product_id': row.get('product_id'),
            'product_name': row.get('product_name'),
            'product_type_id': row.get('product_type_id'),
            'domain': row.get('domain'),
        }
    })
return out
