import json
import os
import re
from datetime import datetime, timezone, timedelta

first_item = _input.first()
payload = first_item.json if first_item else None

BLOCK_START = "PT_STATE_JSON_START"
BLOCK_END = "PT_STATE_JSON_END"
VALID_STATES = {
    'new', 'subdomains_running', 'subdomains_done',
    'nmap_running', 'nmap_done', 'targets_ready',
    'acu_running', 'done', 'error'
}


def read_key(container, key, default=None):
    try:
        value = container.get(key)
        return default if value is None else value
    except Exception:
        try:
            value = container[key]
            return default if value is None else value
        except Exception:
            return default


def to_list(value):
    if value is None:
        return []
    if isinstance(value, list):
        return value
    try:
        return [v for v in value]
    except Exception:
        return []


def to_container(value):
    if value is None:
        return {}
    if isinstance(value, list):
        return value[0] if value else {}
    return value


def parse_state(description):
    if not isinstance(description, str):
        return None
    pattern = re.compile(rf"{BLOCK_START}\n(.*?)\n{BLOCK_END}", re.DOTALL)
    m = pattern.search(description)
    if not m:
        return None
    try:
        data = json.loads(m.group(1).strip())
        if not isinstance(data, dict):
            return None
        return data
    except Exception:
        return None


def build_description(base_description, state_payload):
    text = base_description if isinstance(base_description, str) else ''
    pattern = re.compile(rf"\n?{BLOCK_START}\n.*?\n{BLOCK_END}\n?", re.DOTALL)
    clean = re.sub(pattern, "\n", text).rstrip()
    block = f"{BLOCK_START}\n{json.dumps(state_payload, ensure_ascii=False, sort_keys=True)}\n{BLOCK_END}"
    if clean:
        return clean + "\n\n" + block
    return block


def normalize_state(raw_state):
    state = str(raw_state or 'new')
    return state if state in VALID_STATES else 'new'


def parse_int(value, default):
    try:
        val = int(value)
        return val if val > 0 else default
    except Exception:
        return default


def parse_ts(value):
    if not isinstance(value, str) or not value.strip():
        return None
    raw = value.strip()
    if raw.endswith('Z'):
        raw = raw[:-1] + '+00:00'
    try:
        dt = datetime.fromisoformat(raw)
        if dt.tzinfo is None:
            dt = dt.replace(tzinfo=timezone.utc)
        return dt.astimezone(timezone.utc)
    except Exception:
        return None


incoming = to_container(payload)
trigger_input = to_container(read_key(incoming, 'trigger', {}))
trigger_product_type_id = read_key(trigger_input, 'product_type_id')
trigger_domain = read_key(trigger_input, 'domain')

try:
    trigger_product_type_id = int(trigger_product_type_id) if trigger_product_type_id is not None else None
except Exception:
    trigger_product_type_id = None

pt_window_size = parse_int(read_key(trigger_input, 'pt_window_size', None), parse_int(os.getenv('PT_WINDOW_SIZE'), 1))
subdomains_limit = parse_int(read_key(trigger_input, 'subdomains_concurrency', None), parse_int(os.getenv('SUBDOMAINS_CONCURRENCY'), 2))
nmap_limit = parse_int(read_key(trigger_input, 'nmap_concurrency', None), parse_int(os.getenv('NMAP_CONCURRENCY'), 5))
subdomains_stale_minutes = parse_int(read_key(trigger_input, 'subdomains_running_timeout_minutes', None), parse_int(os.getenv('SUBDOMAINS_RUNNING_TIMEOUT_MINUTES'), 60))

product_types = to_list(read_key(incoming, 'product_types', []))
products = to_list(read_key(incoming, 'products', []))

pt_list = []
for pt in product_types:
    pt_id = read_key(pt, 'id')
    try:
        pt_id = int(pt_id)
    except Exception:
        continue
    if trigger_product_type_id is not None and pt_id != trigger_product_type_id:
        continue
    pt_list.append(pt)

now = datetime.now(timezone.utc)
subdomains_running_count = 0
recovered_stale_subdomains = 0

prepared_pts = []
for pt in sorted(pt_list, key=lambda x: int(read_key(x, 'id', 0) or 0)):
    pt_id = int(read_key(pt, 'id'))
    original_description = read_key(pt, 'description', '')
    state_obj = parse_state(original_description) or {}
    state = normalize_state(state_obj.get('state'))
    counters = state_obj.get('counters') if isinstance(state_obj.get('counters'), dict) else {}
    retry_count = int(state_obj.get('retry_count', 0) or 0)

    if state == 'subdomains_running':
        last_update_dt = parse_ts(state_obj.get('last_update'))
        stale = False
        if last_update_dt is None:
            stale = True
        else:
            stale = now - last_update_dt > timedelta(minutes=subdomains_stale_minutes)

        if stale:
            recovered_stale_subdomains += 1
            retry_count += 1
            state = 'error'
            updated = {
                'version': 1,
                'state': 'error',
                'counters': counters,
                'last_update': now.isoformat(),
                'retry_count': retry_count,
                'last_error': f'Recovered stale subdomains_running on restart (timeout {subdomains_stale_minutes}m).',
            }
            prepared_pts.append({'pt': pt, 'state': state, 'state_obj': updated, 'original_description': original_description})
            continue

        subdomains_running_count += 1

    prepared_pts.append({'pt': pt, 'state': state, 'state_obj': state_obj, 'original_description': original_description})

subdomains_slots = max(0, subdomains_limit - subdomains_running_count)

eligible = []
for row in prepared_pts:
    if row['state'] in ('new', 'error', 'subdomains_done', 'nmap_done', 'targets_ready'):
        eligible.append(row)

selected_rows = eligible[:pt_window_size]
selected_ids = set(int(read_key(row['pt'], 'id')) for row in selected_rows)

queue_wf_a_subdomains_pt = []
queue_wf_b_nmap_product = []
queue_wf_c_targets_for_pt = []
queue_wf_d_pt_acunetixscan = []
state_updates = []

for row in prepared_pts:
    if row.get('state_obj', {}).get('state') == 'error' and row['state'] == 'error' and 'Recovered stale subdomains_running' in str(row['state_obj'].get('last_error', '')):
        pt_id = int(read_key(row['pt'], 'id'))
        state_updates.append({
            'product_type_id': pt_id,
            'description': build_description(row['original_description'], row['state_obj']),
            'state': 'error',
        })

for row in selected_rows:
    pt = row['pt']
    pt_id = int(read_key(pt, 'id'))
    pt_name = read_key(pt, 'name')
    original_description = row['original_description']
    state_obj = row['state_obj'] if isinstance(row['state_obj'], dict) else {}
    counters = state_obj.get('counters') if isinstance(state_obj.get('counters'), dict) else {}
    retry_count = int(state_obj.get('retry_count', 0) or 0)
    current_state = row['state']

    next_state = None
    queue_name = None
    if current_state in ('new', 'error'):
        if subdomains_slots > 0:
            next_state = 'subdomains_running'
            queue_name = 'a'
            subdomains_slots -= 1
    elif current_state == 'subdomains_done':
        next_state = 'nmap_running'
        queue_name = 'b'
    elif current_state == 'nmap_done':
        next_state = 'targets_ready'
        queue_name = 'c'
    elif current_state == 'targets_ready':
        next_state = 'acu_running'
        queue_name = 'd'

    if queue_name == 'a':
        queue_wf_a_subdomains_pt.append({'product_type_id': pt_id, 'product_type_name': pt_name, 'domain': trigger_domain})
    elif queue_name == 'b':
        jobs = []
        for product in products:
            try:
                product_pt = int(read_key(product, 'prod_type'))
                product_id = int(read_key(product, 'id'))
            except Exception:
                continue
            if product_pt != pt_id:
                continue
            jobs.append({
                'product_id': product_id,
                'product_name': read_key(product, 'name'),
                'product_type_id': pt_id,
                'domain': trigger_domain
            })
        queue_wf_b_nmap_product.extend(jobs[:nmap_limit])
    elif queue_name == 'c':
        queue_wf_c_targets_for_pt.append({'product_type_id': pt_id, 'product_type_name': pt_name, 'domain': trigger_domain})
    elif queue_name == 'd':
        queue_wf_d_pt_acunetixscan.append({'product_type_id': pt_id, 'product_type_name': pt_name, 'domain': trigger_domain})

    if next_state:
        stage_key = {
            'subdomains_running': 'subdomains_runs',
            'nmap_running': 'nmap_runs',
            'targets_ready': 'targets_runs',
            'acu_running': 'acu_runs',
        }.get(next_state)
        counters[stage_key] = int(counters.get(stage_key, 0) or 0) + 1
        updated = {
            'version': 1,
            'state': next_state,
            'counters': counters,
            'last_update': now.isoformat(),
            'retry_count': retry_count,
            'last_error': None if next_state == 'subdomains_running' else state_obj.get('last_error'),
        }
        state_updates.append({
            'product_type_id': pt_id,
            'description': build_description(original_description, updated),
            'state': next_state,
        })

return [{
    'json': {
        'run_subdomains': len(queue_wf_a_subdomains_pt) > 0,
        'run_nmap': len(queue_wf_b_nmap_product) > 0,
        'run_targets': len(queue_wf_c_targets_for_pt) > 0,
        'run_acunetix': len(queue_wf_d_pt_acunetixscan) > 0,
        'queue_wf_a_subdomains_pt': queue_wf_a_subdomains_pt,
        'queue_wf_b_nmap_product': queue_wf_b_nmap_product,
        'queue_wf_c_targets_for_pt': queue_wf_c_targets_for_pt,
        'queue_wf_d_pt_acunetixscan': queue_wf_d_pt_acunetixscan,
        'state_updates': state_updates,
        'trigger_product_type_id': trigger_product_type_id,
        'trigger_domain': trigger_domain,
        'pt_window_size': pt_window_size,
        'selected_pt_ids': sorted(list(selected_ids)),
        'subdomains_concurrency': subdomains_limit,
        'subdomains_running_now': subdomains_running_count,
        'subdomains_slots_available': max(0, subdomains_limit - subdomains_running_count),
        'subdomains_stale_recovered': recovered_stale_subdomains,
        'subdomains_running_timeout_minutes': subdomains_stale_minutes,
        'nmap_concurrency': nmap_limit,
        'stage_barriers': {
            'subdomains_done_after_all_jobs': True,
            'nmap_done_after_all_jobs': True,
        },
    }
}]

