import os, json, urllib.request
raw = (os.environ.get('ACUNETIX_INSTANCES_JSON') or '').strip()
nodes = []
if raw:
    try:
        arr = json.loads(raw)
        if isinstance(arr, list):
            nodes = [i for i in arr if isinstance(i, dict) and i.get('endpoint') and i.get('token')]
    except Exception:
        nodes = []
if not nodes:
    nodes = [{'name':'acu-default','endpoint': os.environ.get('ACUNETIX_BASE_URL','https://localhost:3443'),'token': (os.environ.get('ACUNETIX_API_KEY') or os.environ.get('ACU_API_TOKEN') or '')}]

reports = []
healthy = 0
for idx, n in enumerate(nodes):
    endpoint = str(n.get('endpoint') or '').rstrip('/')
    token = str(n.get('token') or '')
    name = n.get('name') or f'acu-{idx+1}'
    status_code = None
    ok = False
    if endpoint and token:
        try:
            req=urllib.request.Request(endpoint+'/api/v1/me')
            req.add_header('X-Auth', token)
            with urllib.request.urlopen(req, timeout=6) as resp:
                status_code = getattr(resp,'status',200)
                ok = 200 <= status_code <= 299
        except Exception:
            ok = False
    reports.append({'name':name,'endpoint':endpoint,'statusCode':status_code,'ok':ok})
    if ok:
        healthy += 1

return [{'json': {'statusCode': 200 if healthy>0 else 503, 'healthy_nodes': healthy, 'total_nodes': len(reports), 'nodes': reports}}]

from datetime import datetime, timezone
import json
import os
import re
import urllib.request

now = datetime.now(timezone.utc).isoformat()
BLOCK_START = 'PT_STATE_JSON_START'
BLOCK_END = 'PT_STATE_JSON_END'
VALID_STATES = {'new','subdomains_running','subdomains_done','nmap_running','nmap_done','targets_ready','acu_running','done','error'}


def status_in_ranges(status_code, ranges):
    if status_code is None:
        return False
    for low, high in ranges:
        if low <= status_code <= high:
            return True
    return False


def parse_state(description):
    if not isinstance(description, str):
        return {}
    m = re.search(rf"{BLOCK_START}\n(.*?)\n{BLOCK_END}", description, re.DOTALL)
    if not m:
        return {}
    try:
        payload = json.loads(m.group(1).strip())
        return payload if isinstance(payload, dict) else {}
    except Exception:
        return {}


def to_results(node_name):
    data = $node[node_name].json if $node[node_name].json else {}
    if isinstance(data, dict):
        return data
    return {}


def fetch_next_pages(initial_next):
    token = (os.environ.get('DOJO_API_TOKEN') or '').strip()
    if not initial_next or not token:
        return []
    out = []
    visited = set()
    current = str(initial_next)
    while current and current not in visited:
        visited.add(current)
        req = urllib.request.Request(current)
        req.add_header('Authorization', f'Token {token}')
        req.add_header('Accept', 'application/json')
        with urllib.request.urlopen(req, timeout=20) as resp:
            payload = json.loads((resp.read() or b'{}').decode('utf-8'))
        out.extend(payload.get('results') or [])
        current = payload.get('next')
    return out

services = [
    ('dojo', 'Check Dojo', [(200, 299)]),
    ('acunetix', 'Check Acunetix Node', [(200, 299)]),
    ('n8n', 'Check n8n', [(200, 299)]),
]

report = {}
critical = []
log_events = []
node_errors = []

for service_name, node_name, expected_ranges in services:
    node_data = to_results(node_name)
    status_code = node_data.get('statusCode')
    ok = status_in_ranges(status_code, expected_ranges)
    error_message = None
    if status_code is None:
        error_message = 'no_status_code_returned'
    elif not ok:
        error_message = f'unexpected_status_code:{status_code}'
    report[service_name] = {
        'ok': ok,
        'status_code': status_code,
        'error_message': error_message,
        'checked_at': now,
    }
    log_events.append({
        'pt_id': None,
        'stage': f'health_{service_name}',
        'job_id': service_name,
        'server': service_name,
        'status': 'ok' if ok else 'error',
        'duration': None,
    })
    if not ok:
        critical.append(f'{service_name}_unavailable')
        node_errors.append({'node': service_name, 'error': error_message})

acu_data = to_results('Check Acunetix Node')
for n in acu_data.get('nodes', []) or []:
    if not n.get('ok'):
        node_errors.append({'node': n.get('name'), 'error': f"statusCode:{n.get('statusCode')}"})
    log_events.append({
        'pt_id': None,
        'stage': 'acu_node_health',
        'job_id': n.get('name'),
        'server': n.get('endpoint') or n.get('name'),
        'status': 'ok' if n.get('ok') else 'error',
        'duration': None,
    })

pt_payload = to_results('Get PT States')
product_types = list(pt_payload.get('results') or [])
product_types.extend(fetch_next_pages(pt_payload.get('next')))
pt_window = int(os.environ.get('PT_HEALTH_WINDOW_SIZE', '300') or 300)
product_types = product_types[:max(1, pt_window)]

state_counts = {state: 0 for state in VALID_STATES}
queues = {'subdomains': 0, 'nmap': 0, 'acu': 0}
active_slots = {'subdomains': 0, 'nmap': 0, 'acu': 0}
pt_errors = []

for pt in product_types:
    pt_id = pt.get('id')
    state_obj = parse_state(pt.get('description', ''))
    state = str(state_obj.get('state') or 'new')
    if state not in VALID_STATES:
        state = 'new'
    state_counts[state] = state_counts.get(state, 0) + 1

    if state in {'new', 'error'}:
        queues['subdomains'] += 1
    if state in {'subdomains_done', 'nmap_running'}:
        queues['nmap'] += 1
    if state in {'targets_ready', 'acu_running'}:
        queues['acu'] += 1

    if state == 'subdomains_running':
        active_slots['subdomains'] += 1
    if state == 'nmap_running':
        active_slots['nmap'] += 1
    if state == 'acu_running':
        active_slots['acu'] += 1

    if state == 'error':
        err = str(state_obj.get('last_error') or 'unknown_error')
        stage = str(state_obj.get('last_stage') or 'unknown_stage')
        pt_errors.append({'pt_id': pt_id, 'stage': stage, 'error': err})
        log_events.append({
            'pt_id': pt_id,
            'stage': stage,
            'job_id': f'pt:{pt_id}',
            'server': 'dojo:pt_state',
            'status': 'error',
            'duration': None,
        })

max_events = int(os.environ.get('HEALTH_MAX_LOG_EVENTS', '500') or 500)
if max_events > 0:
    log_events = log_events[:max_events]

return [{
  'json': {
    **report,
    'critical': critical,
    'has_critical': len(critical) > 0,
    'checked_at': now,
    'pt_window_size': pt_window,
    'pt_state_counts': state_counts,
    'queue_status': queues,
    'active_slots': active_slots,
    'pt_errors': pt_errors,
    'node_errors': node_errors,
    'log_events': log_events,
  }
}]
